/* Structure for the `wp_options` table : */

CREATE TABLE `wp_options` (
  `option_id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `option_name` VARCHAR(191) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `option_value` LONGTEXT COLLATE utf8mb4_unicode_ci NOT NULL,
  `autoload` VARCHAR(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'yes',
  PRIMARY KEY USING BTREE (`option_id`),
  UNIQUE KEY `option_name` USING BTREE (`option_name`),
  KEY `autoload` USING BTREE (`autoload`)
) ENGINE=InnoDB
AUTO_INCREMENT=311 ROW_FORMAT=DYNAMIC CHARACTER SET 'utf8mb4' COLLATE 'utf8mb4_unicode_ci'
;

